import React, { useEffect, useState } from 'react';
import api from '../../services/api'

import './styles.css'

export default function MainList(){
    async function listar(){
        let user_id = localStorage.getItem('user');
        let page = 0;
        const listUsers = await api.get('/listUsers', { page, user_id });
        const listBrokers = await api.get('/listBrokers', { page, user_id });
    }
    const [investors, setInvestors] = useState([]);
    const [brokers, setBrokers] = useState([]);
    useEffect(()=>{
        async function loadUsers(){
            const response = await api.get('/obt_ivestidor');
            setInvestors(response.data)
        }
        async function loadInvestors(){
            const response = await api.get('/obt_corretor');
            setBrokers(response.data)
        }
        loadUsers();
        loadInvestors();
    });
    return (
        <>
            <div className="listDiv" onLoad={listar}>
                <label htmlFor="investors-list">Lista de investidores</label>
                <ul className='list' id='investors-list'>
                    {investors.map(investor => (
                        <li className='list-item'>
                            <strong>{investor.name}</strong>
                            <ul className='list'> 
                                <li className='list-option'>{investor.phone}</li>
                                <li className='list-option'>{investor.email}</li>
                            </ul>
                        </li>
                    ))}
                </ul>
            </div>
                <div className="listDiv">
                <label htmlFor="brokers-list">Lista de usu√°rios</label>
                <ul className='list' id='brokers-list'>
                    {brokers.map(broker => (
                        <li className='list-item'>
                            <strong>{broker.name}</strong>
                            <ul className='list'> 
                                <li className='list-option'>{broker.phone}</li>
                                <li className='list-option'>{broker.email}</li>
                            </ul>
                        </li>
                    ))}
                </ul>
            </div>
        </>
    )
}
