import React, { useEffect, useState } from 'react';
import api from '../../services/api'
import dropDown from '../../assets/angle-down-solid.svg';

import './styles.css'

export default function MainList(){
    async function listar(){
        let user_id = localStorage.getItem('user');
        let page = 0;
    }
    const [investors, setInvestors] = useState([]);
    const [brokers, setBrokers] = useState([]);
    useEffect(()=>{
        async function loadUsers(){
            const response = await api.get('/listUsers');
            setInvestors(response.data)
        }
        async function loadInvestors(){
            const response = await api.get('/listBrokers');
            setBrokers(response.data)
        }
        loadUsers();
        loadInvestors();
    });
    return (
        <>
            <div className="listDiv" onLoad={listar}>
                <label htmlFor="investors-list">Lista de investidores</label>
                <ul className='list' id='investors-list'>
                    {investors.map(investor => (
                        <li className='list-item' key={investor._id}>
                            <strong>{investor.name}</strong>
                            <ul className='list'> 
                                <li className='user-option'>{investor.phone}</li>
                                <li className='user-option'>{investor.email}</li>
                            </ul>
                        </li>
                    ))}
                </ul>
            </div>
                <div className="listDiv">
                <label htmlFor="brokers-list">Lista de investidores</label>
                <ul className='list' id='brokers-list'>
                    {brokers.map(broker => (
                        <li className='list-item' key={broker._id}>
                            <strong>{broker.name}</strong>
                            <div className="broker-menu">
                                <input type="checkbox" name="check-broker-options" id="check-broker-options"/>
                                <label htmlFor="check-broker-options" id='icone'><img src={dropDown} id='dropdown-img' alt="dropdown menu"/></label>
                                <ul className='list'> 
                                    <li className='broker-option'>{broker.phone}</li>                                
                                    <li className='broker-option'>{broker.email}</li>
                                </ul>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
        </>
    )
}
