import React, { useState } from 'react';
import api from '../../services/api'
import validarCpf from '../../verificaCpf'

export default function Cadastro(){
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [pwd, setPwd] = useState('');
    const [tel, setTel] = useState('');
    const [cpf, setPCpf] = useState('');
    const [birthday, setBirthday] = useState('');
    const [sex, setSex] = useState('');
    const [broker, setBroker] = useState(false);

    function handleCheckboxChange(){
        let corretor = document.querySelector('#corretor');

        if (corretor.checked) {
            setBroker(true)
        }else{
            setBroker(false)
        }

    }
    function handleCpfChange(){
        let cpf = document.querySelector('#cpf');

        validarCpf.TestaCPF(cpf.value);
    }
    async function handleSubmit(){

    }



    return (
        <>
            <p>Cadastro</p>

            <form onSubmit={handleSubmit}>
            <label htmlFor="firstName">Quem é você</label>
                <input 
                    type="text"
                    id="firstName"
                    placeholder="Primeiro nome..."
                    value={firstName}
                    onChange={event => setFirstName(event.target.value)}
                    required
                />
                <input 
                    type="text"
                    id="lastName"
                    placeholder="Sobrenome..."
                    value={lastName}
                    onChange={event => setLastName(event.target.value)}
                />

                <label htmlFor="email">E-Mail</label>
                <input 
                    type="email"
                    id="email"
                    placeholder="example@example.com"
                    value={email}
                    onChange={event => setEmail(event.target.value)}
                    required
                />

                <label htmlFor="pwd">Senha</label>
                <input 
                    type="password" 
                    id="pwd"
                    value={pwd}
                    onChange={event => setPwd(event.target.value)}
                    required
                />

                <label htmlFor="telefone">Telefone</label>
                <input 
                    type="tel"
                    id="telefone"
                    placeholder="Somente numeros..."
                    value={tel}
                    onChange={event => setTel(event.target.value)}
                    required
                />

                <label htmlFor="telefone">Telefone</label>
                <input 
                    type="tel"
                    id="telefone"
                    placeholder="Somente numeros..."
                    value={tel}
                    onChange={event => setTel(event.target.value)}
                    required
                />

                <label htmlFor="cpf">CPF</label>
                <input 
                    type="number"
                    id="cpf"
                    placeholder="Somente numeros..."
                    value={cpf}
                    onChange={handleCpfChange}
                    required
                />
                <div className="checkbox-corretor">
                    <input 
                        type="checkbox" 
                        name="corretor" 
                        id="corretor"/>
                    <label
                        htmlFor="corretor" 
                        name='labelCorretor'
                        onChange={handleCheckboxChange} 
                        >Sou corretor de investimentos</label>
                </div>
                <button type="submit" className="btn">Entrar</button>
            </form>
        </>
    )
}
